import { useState } from 'react';

const useForm = () => {
  const [inputFields, setInputFields] = useState({
    username: '',
    password: '',
    confirmPassword: ''
  });

  const [inputErrors, setInputErrors] = useState({
    username: [],
    password: [],
    confirmPassword: []
  });

  const checkForInputErrors = (fieldName, fieldValue) => {
    switch (fieldName) {
      case 'username': {
        const errors = [];
        if (fieldValue.includes('')) {
            errors.push("Username cannot contain spaces");
        }
      }
    }
  };

  const updateInputFields = field => {
    const fieldName = field.target.name;
    const fieldValue = field.target.value;

    checkForInputErrors(fieldName, fieldValue);

    setInputFields({ ...inputFields, [fieldName]: fieldValue });
  };

  return { updateInputFields, inputFields, inputErrors };
};

export default useForm;
