import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import watchListLogo from '../img/watchlist-logo.svg';
import AuthError from './AuthError';
import '../css/register.scss';

const Register = () => {
  const [registerFields, setRegisterFields] = useState({
    username: '',
    password: '',
    confirmPassword: ''
  });

  const [registerErrors, setRegisterErrors] = useState({
    username: [],
    password: [],
    confirmPassword: []
  });

  const checkForInputErrors = event => {
    const field = event.target;

    setRegisterErrors({
      username: [],
      password: [],
      confirmPassword: []
    });

    switch (field.name) {
      case 'username': {
        const errors = [];
        if (registerFields.username.length < 1) {
          errors.push('Username is required');
        }

        if (registerFields.username.includes(' ')) {
          errors.push('Username cannot contain any spaces');
        }

        setRegisterErrors({
          ...registerErrors,
          username: errors
        });
        break;
      }
      case 'password': {
        const errors = [];
        if (registerFields.password.length < 1) {
          errors.push('Password is required');
        }

        if (registerFields.password.includes(' ')) {
          errors.push('Password cannot contain any spaces');
        }

        if (registerFields.password.length < 6) {
          errors.push('Password needs to have 6 or more characters');
        }

        setRegisterErrors({
          ...registerErrors,
          password: errors
        });
        break;
      }

      case 'confirmPassword': {
        const errors = [];
        if (registerFields.password !== registerFields.confirmPassword) {
          errors.push('Passwords do not match');
        }
        setRegisterErrors({
          ...registerErrors,
          confirmPassword: errors
        });
      }
    }
  };

  const handleFormFields = event => {
    const fieldName = event.target.name;
    const fieldValue = event.target.value;
    switch (fieldName) {
      case 'username':
        setRegisterFields({ ...registerFields, username: fieldValue });
        break;

      case 'password':
        setRegisterFields({ ...registerFields, password: fieldValue });
        break;

      case 'confirmPassword':
        setRegisterFields({ ...registerFields, confirmPassword: fieldValue });
        break;
    }
  };

  return (
    <div className="register">
      <div className="register-header">
        <img
          className="register-header__logo"
          src={watchListLogo}
          alt="Smiling TV"
        />
        <h1 className="register-header__title">Watch List</h1>
      </div>

      <form className="register-form">
        <AuthError registerErrors={registerErrors} />
        <label className="username-label">
          <span className="username-label__text">Username</span>
          <input
            className="register-form__username"
            type="text"
            name="username"
            value={registerFields.username}
            onChange={handleFormFields}
            onBlur={checkForInputErrors}
          />
        </label>
        <label>
          <span>Password</span>
          <input
            className="register-form__password"
            type="password"
            name="password"
            value={registerFields.password}
            onChange={handleFormFields}
            onBlur={checkForInputErrors}
          />
        </label>
        <label>
          <span>Confirm Password</span>
          <input
            className="register-form__confirm-password"
            type="password"
            name="confirmPassword"
            value={registerFields.confirmPassword}
            onChange={handleFormFields}
            onBlur={checkForInputErrors}
          />
        </label>
        <input
          className="register-form__submit"
          type="submit"
          value="Register"
        />
      </form>

      <div className="register-links">
        <div className="register-signin">
          <p className="register-signin__message">Already Have An Account?</p>
          <Link to="/login" className="register-signin__link">
            Sign In
          </Link>
        </div>
        <Link to="/" className="register-links__guest">
          Guest
        </Link>
      </div>
    </div>
  );
};

export default Register;
