import { useState } from 'react';

const useForm = () => {
  const [inputFields, setInputFields] = useState({
    username: '',
    password: '',
    confirmPassword: ''
  });

  const updateFields = field => {
    const fieldValue = field.value;
    switch (field) {
      case 'username': {
          console.log("hey");
        setInputFields({ username: fieldValue });
        break;
      }
    }
  };

  return { updateFields, inputFields };
};

export default useForm;
