import { useState } from 'react';

const useForm = () => {
  const [inputFields, setInputFields] = useState({
      username:"",
      password:"",
      confirmPassword:""
  });

  const updateFields = field => {
    const fieldValue = field.target.value;
    const fieldName = field.target.name

    switch (fieldName) {
      case 'username': {
        setInputFields({...inputFields, username: fieldValue });
        break;
      }
      case'password':{
          setInputFields({})
      }
    }
  };

  return { updateFields, inputFields };
};

export default useForm;
