import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import watchListLogo from '../img/watchlist-logo.svg';
import AuthError from './AuthError';
import '../css/register.scss';

const Register = () => {
  const [formFields, setFormFields] = useState({
    username: '',
    password: '',
    confirmPassword: ''
  });

  const [errors,setErrors] = useState([
    {
      username:{
        error:false,
        msg:""
      }
    }
    ]);

  const handleInputChange = event => {
    const fieldName = event.target.name;
    const fieldValue = event.target.value;
    switch (fieldName) {
      case 'username':
        setFormFields({ ...formFields, username: fieldValue });
        return;

      case 'password':
        setFormFields({ ...formFields, password: fieldValue });
        return;

      case 'confirmPassword':
        setFormFields({ ...formFields, confirmPassword: fieldValue });
        return;
    }
  };

  return (
    <div className="register">
      <div className="register-header">
        <img
          className="register-header__logo"
          src={watchListLogo}
          alt="Smiling TV"
        />
        <h1 className="register-header__title">Watch List</h1>
      </div>

      <form className="register-form">
        <AuthError />
        <input
          className="register-form__username"
          type="text"
          placeholder="Username"
          name="username"
          value={formFields.username}
          onChange={handleInputChange}
        />
        <input
          className="register-form__password"
          type="password"
          placeholder="Password"
          name="password"
          value={formFields.password}
          onChange={handleInputChange}
        />

        <input
          className="register-form__confirm-password"
          type="password"
          placeholder="Confirm Password"
          name="confirmPassword"
          value={formFields.confirmPassword}
          onChange={handleInputChange}
        />
        <input
          className="register-form__submit"
          type="submit"
          value="Register"
        />
      </form>

      <div className="register-links">
        <div className="register-signin">
          <p className="register-signin__message">Already Have An Account?</p>
          <Link to="/login" className="register-signin__link">
            Sign In
          </Link>
        </div>
        <Link to="/" className="register-links__guest">
          Guest
        </Link>
      </div>
    </div>
  );
};

export default Register;
