import React from 'react';
import '../css/auth-error.scss';
import inputErrorIcon from '../img/input-error-icon.svg';

const AuthError = ({ registerFieldErrors }) => {
  let errors = [];

  for (const inputField in registerErrors) {
    errors = [...errors, ...registerErrors[inputField]];
  }

  let err = errors.map((item, index) => {
    return (
      <li key={index.toString()} className="auth-error-list__item">
        <div className="auth-error-wrapper">
          <img
            className="auth-error-wrapper__input-status"
            src={inputErrorIcon}
            alt="Input Status Icon"
          />
          <p className="auth-error-wrapper__text">{item}</p>
        </div>
      </li>
    );
  });

  return (
    <div className="auth-error">
      {errors.length > 0 && <p className="auth-error__message">Please fix the following errors:</p>}
      <ul className="auth-error-list">{err}</ul>
    </div>
  );
};

export default AuthError;
